import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import device from "./styles/device";
import sizes from "./styles/device-sizes";
import imgUrls from "../assets/imgUrls";

//TODO: this should be a function that takes in a list of images and does the same thing for all of them

const ImageSlide = styled.img`
    width: 100%;
    -webkit-filter: blur(15px); /* Safari 6.0 - 9.0 */
    filter: blur(15px);
    height: 500px;
    margin: 0px;
    padding: 0px;
    grid-row: 1 / 2;
    grid-column: 1 / 5;
`;



// const Arrow = ({ direction, clickFunction, glyph }) => (
//     <div
//       className={ `slide-arrow ${direction}` }
//       onClick={ clickFunction }>
//       { glyph }
//     </div> 
//   );

const CarouselWrapper = styled.div`
    //      |<|img|text|>|


`;

const OverLay = styled.div`
    grid-template-columns: auto 300px 700px auto;
    grid-template-rows: auto;
    background-color: rgba(0, 0, 0, 0.3);
    display: grid;  
    align-content: center;
    justify-items: center;
    align-items: center;
    justify-content: center;
    // grid-column: 1 / 5;
    // grid-row: 1 / 2;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 500px;
    margin: 0px;
    padding: 0px;
`;

const ArrowLeft = styled.p`
    // position: absolute;
    grid-column: 1 / 2;
    // top: 100px;
    color: gray;
    // left: 10px;
    font-size: 70px;

    :hover {
        cursor: pointer;
    }
`;

const ArrowRight = styled.p`
    grid-column: 4 / 5;
    color: gray;
    font-size: 70px;
`;

const ImgThumbnail = styled.img`
    width: 150px;
    height: 250px;
    grid-column: 2 / 3;
`;

class Carousel extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
          currentImageIndex: 0
        };
        this.nextSlide = this.nextSlide.bind(this);
        this.previousSlide = this.previousSlide.bind(this);
      }

    previousSlide () {
        const lastIndex = imgUrls.length - 1;
        const { currentImageIndex } = this.state;
        const shouldResetIndex = currentImageIndex === 0;
        const index =  shouldResetIndex ? lastIndex : currentImageIndex - 1;

        this.setState({
            currentImageIndex: index
        });
    }

    nextSlide () {
        const lastIndex = imgUrls.length - 1;
        const { currentImageIndex } = this.state;
        const shouldResetIndex = currentImageIndex === lastIndex;
        const index =  shouldResetIndex ? 0 : currentImageIndex + 1;

        this.setState({
            currentImageIndex: index
        });
    }

    render () {
        console.log("hi");
        console.log(imgUrls[0]);
      return (
        <CarouselWrapper>
          <ImageSlide src={imgUrls[this.state.currentImageIndex]} />
          <OverLay>
            <ArrowLeft
                id="left"
                onClick={ this.prevSlide }> &#60; </ArrowLeft>
            <ImgThumbnail src={imgUrls[this.state.currentImageIndex]}></ImgThumbnail>
            {/* <TextThumbnail>{textBlurb[]}</TextThumbnail> */}
            <ArrowRight
                id="right"
                onClick={ this.nextSlide }>
                &#62;
                </ArrowRight>
          </OverLay>
        </CarouselWrapper>
      );
    }
  }

  export default Carousel;
