import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import device from "./styles/device";
import sizes from "./styles/device-sizes";

import { createTypeAnnotationBasedOnTypeof } from "@babel/types";

const ThumbnailWrapper = styled.div`
  display: grid;
//   align-content: center;
  justify-items: center;
  align-items: center;
  justidy-content: center;

  @media ${device.tablet} {
    padding: 100px;
    grid-template-columns: 50px auto 50px;
    grid-template-rows: auto auto;
  }

  @media ${device.laptop} {
    padding: 120px 50px;
    grid-template-columns: auto 500px 50px 500px auto;
    grid-template-rows: auto;
  }
`;

const Left = styled.div`
    @media ${device.tablet} {
        grid-column: 2 / 3;
        grid-row: 2 / 3;
    }

    @media ${device.laptop} {
        width: 500px;
        grid-column: 2 / 3;
        grid-row: 1 / 2;
    }
`;

const Right = styled.div`
    @media ${device.tablet} {
        grid-column: 2 / 3;
        grid-row: 1 / 2;
    }

    @media ${device.laptop} {
        grid-column: 4 / 5;
        grid-row: 1 / 2;
    }
`;


function renderOrder(props) {
    console.log(window.innerWith);
    console.log(window.innerWidth < 1220);
    console.log(sizes.laptop);
    console.log(device.laptop);
    console.log(window.innerWidth < sizes.laptop);

    if(props.imgFirst || (window.innerWidth < 1220)) {
      return (
        <React.Fragment>
            <Left><img src={props.imgSrc}></img></Left>
            <Right>
                <TextBlurb 
                    title={props.title} 
                    text={props.text}>
                </TextBlurb>
            </Right>
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
            <Left>
                <TextBlurb 
                    title={props.title} 
                    text={props.text}>
                </TextBlurb>
            </Left>
            <Right> <img src={props.imgSrc}></img></Right>
        </React.Fragment>
      );
    }
  }

export const Thumbnail = ({ ...props }) => (
    <ThumbnailWrapper style={props.style}>
        {renderOrder(props)}
    </ThumbnailWrapper>
)

export default Thumbnail;
