import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import device from "./device";
import { createTypeAnnotationBasedOnTypeof } from "@babel/types";

const ThumbnailWrapper = styled.div`
  display: grid;
  justify-content: center;
  align-content: center;
  align-items: center;

  @media (min-width: 300px) {
    padding: 100px;
    grid-template-columns: auto 600px auto;
    grid-template-rows: auto auto;
  }

  @media (min-width: 1220px){
    padding: 100px;
    grid-template-columns: auto 500px 50px 500px auto;
    grid-template-rows: auto;
  }
`;

const ThumbnailImg = styled.img`
    @media (min-width: 300px) {
        grid-column: 2 / 3;
        grid-row: 2 / 3;
    }

    @media (min-width: 1220px){
        width: 500px;
        grid-column: 2 / 3;
        grid-row: 1 / 2;
    }
`;

function renderOrder(props) {
    console.log("hi");
    //BELOW IS NOT THE SOLUTION FOR LEFT/RIGHT
    console.log(props.imgFirst);
    if(props.imgFirst) {
      return (
        <React.Fragment>
            <ThumbnailImg src={props.imgSrc}></ThumbnailImg>
            <TextBlurb
                title={props.title} 
                text={props.text}/>
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
            <TextBlurb
                title={props.title} 
                text={props.text}
                style={{gridColumn: "2 / 3"}}/>
            <ThumbnailImg src={props.imgSrc} style={{gridColumn: "4 / 5"}}></ThumbnailImg>
        </React.Fragment>
      );
    }
  }

export const Thumbnail = ({ ...props }) => (
    <ThumbnailWrapper style={props.style}>
        {renderOrder(props)}
    </ThumbnailWrapper>
)

export default Thumbnail;
