import React from "react";
import styled from "styled-components";
import { ThemeProvider } from "styled-components";

const ImageSlide = styled.img`
    width: 100vw;
    -webkit-filter: blur(10px) brightness(40%);
    filter: blur(10px) brightness(40%);
    height: 500px;
    margin: 0px;
    padding: 0px;
`;

const OverLay = styled.div`
    grid-template-columns: auto 300px 500px auto;
    grid-template-rows: auto;
    background-color: ${props => props.theme.transparent};
    display: grid;  
    align-content: center;
    align-items: center;
    justify-content: center;
    position: relative;
    margin-top: -500px;

    width: 100vw;
    height: 500px;
    padding: 0px;
`;

const InfoThumbnail = styled.div`
    vertical-align: top;
    align-items: flex-start;
`;

const Title = styled.h1 `
    margin-top: -80px;
    color: ${colors.white1};
    padding: 0px;
    font-weight: 500;
    grid-column: 3 / 4;
`;

const TextThumbnail = styled.p`
    margin: 25px 0px;
    padding: 0px;
    color: ${colors.white1Transparent};
    grid-column: 3 / 4;
`;

const ImgThumbnail = styled.img`
    justify-self: flex-end;
    width: 250px;
    margin: 50px;
    height: auto;
    grid-column: 2 / 3;
`;

const ButtonContainer = styled.div`
    grid-column: 3 / 4;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    border-radius: 5px;
    margin-right: 10px;
    color: ${colors.white1};
    background-color: ${props => props.theme.main};
    border: none;
    font-size: 17px;
    font-weight: bold;
    >img, >span {
        vertical-align: middle;

    }
    :hover {
        cursor: pointer;
    }
`;

const Icon = styled.img`
    width: 30px;
    padding-right: 10px;
`;

const TrailerButton = styled(Button)`
    background-color: ${colors.transparent};
    border-color: ${colors.white1Transparent};
    border: 1px solid ${colors.white1Transparent};
`;

export const Slide = ({ ...props }) => (
    <ThemeProvider theme={props.thumbnail.theme}>
        <ImageSlide src={props.thumbnail.imgUrl} />
        <OverLay>
            <ImgThumbnail src={props.thumbnail.imgUrl}></ImgThumbnail>
            <InfoThumbnail>
                <Title>{props.thumbnail.title}</Title>
                <TextThumbnail>{props.thumbnail.description}</TextThumbnail>
                <ButtonContainer>
                    <Button>
                        <Icon src={BuyIcon}/>
                        <span>Buy Now</span>
                    </Button>
                    <TrailerButton><span>Watch Trailer</span></TrailerButton>
                </ButtonContainer>
            </InfoThumbnail>
        </OverLay>
    </ThemeProvider>
)

export default Slide;
