import './App.css';
import React, { Component } from 'react';
import TextBlurb from './components/textBlurb';
import Thumbnail from './components/thumbnail';
import device from './components/device';

import styled from 'styled-components';


//USE A GRID!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
//margins should be flexible, content columns static
//use standard breakpoints
//consider how to change order...

const Main = styled.div`
  display: grid;
  grid-template-columns: 100%;
  justify-content: center;
  align-content: center;

  @media (min-width: 300px){
    grid-template-rows: auto auto auto;
  }

  @media (min-width: 1220px){
    grid-template-rows: auto auto auto;
  }
`;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const menuItems = [
      { title: "Title 1", imageSrc: `https://i.vimeocdn.com/video/595198868_505x160.jpg`, text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum." },
      { title: "Title 2", imageSrc: `https://i.vimeocdn.com/video/589972810_530x315.jpg`, text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum." },
      { title: "Title 3", imageSrc: `https://i.vimeocdn.com/video/590587169_530x315.jpg`, text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum." }
    ];  

    return (
      <Main>
        <Thumbnail style={{backgroundColor: "white",
                    color: "black",
                    gridRow: "1 / 2"}}
                    imgSrc={menuItems[0].imageSrc}
                    title={menuItems[0].title} 
                    text={menuItems[0].text}
                    imgFirst={true}>
        </Thumbnail>
        <Thumbnail style={{backgroundColor: "#3a3a3a",
                           color: "white",
                           gridRow: "2 / 3"}}
                    imgSrc={menuItems[1].imageSrc}
                    title={menuItems[1].title} 
                    text={menuItems[1].text}
                    imgFirst={false}>
        </Thumbnail>
        <Thumbnail style={{backgroundImage: "linear-gradient(#3a3a3a, #dcdcdc)",
                    color: "white",
                    gridRow: "3 / 4"}}
                    imgSrc={menuItems[2].imageSrc}
                    title={menuItems[2].title} 
                    text={menuItems[2].text}
                    imgFirst={true}>
        </Thumbnail>
      </Main>
    );
  }
}
export default App;
