import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import { ThemeProvider } from "styled-components";
import device from "./styles/device";
import sizes from "./styles/device-sizes";
import colors from "./styles/palette";
import carouselThumbnails from "../assets/carouselThumbnails";

//TODO: this should be a function that takes in a list of images and does the same thing for all of them

const ImageSlide = styled.img`
    width: 100%;
    -webkit-filter: blur(10px);
    filter: blur(10px);
    height: 500px;
    margin: 0px;
    padding: 0px;
    position: relative;
    top: 0;
    display: inline;
    // grid-row: 1 / 2;
    // grid-column: 1 / 5;
`;

const SlideWrapper = styled.span`
    // marginLeft: -200px;
    // display: inline;
`;

// const Arrow = ({ direction, clickFunction, glyph }) => (
//     <div
//       className={ `slide-arrow ${direction}` }
//       onClick={ clickFunction }>
//       { glyph }
//     </div> 
//   );

const OverLay = styled.div`
    display: inline;
    grid-template-columns: auto 300px 500px auto;
    grid-template-rows: auto auto auto;
    background-color: ${props => props.theme.transparent};
    display: grid;  
    align-content: center;
    // justify-items: center;
    align-items: center;
    justify-content: center;
    // grid-column: 1 / 5;
    // grid-row: 1 / 2;
    position: relative;
    top: 0;
    // left: 0;
    width: 100%;
    height: 500px;
    margin-top: -500px;
    padding: 0px;
`;

const Arrow = styled.div`
    padding: 0px 50px;
    grid-column: ${props => props.right ? "4 / 5" : "1 / 2"};
    grid-row: 2 / 3;
    color: ${colors.white1Transparent};
    font-size: 70px;
    width: 100%;

    :hover {
        cursor: pointer;
        color: ${colors.blue1};
    }
`;

const Title = styled.h1 `
    color: ${colors.white1};
    margin: 0px;
    padding: 0px;
    font-weight: 500;
    grid-column: 3 / 4;
`;

const TextThumbnail = styled.p`
    margin: 0px;
    padding: 0px;
    color: ${colors.white1Transparent};
    grid-column: 3 / 4;
`;

const ImgThumbnail = styled.img`
    width: 150px;
    height: 250px;
    grid-column: 2 / 3;
    grid-row: 1 / 4;
`;

const ButtonContainer = styled.div`
    grid-column: 3 / 4;
    grid-row: 3 / 4;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    border-radius: 5px;
    margin: 10px;
    color: ${colors.white1};
    background-color: ${props => props.theme.main};
    border: none;
    font-size: 17px;
    font-weight: bold;
`;

const TrailerButon = styled(Button)`
  background-color: ${colors.transparent};
  border-color: ${colors.white1Transparent};
  border: 1px solid ${colors.white1Transparent};
`;

const goldTheme = {
    main: colors.gold1,
    transparent: colors.gold1Transparent
}

const blueTheme = {
    main: colors.blue1,
    transparent: colors.blue1Transparent
}

const tealTheme = {
    main: colors.teal1,
    transparent: colors.teal1Transparent
}

const grayTheme = {
    main: colors.charcoal2,
    transparent: colors.charcoal1Transparent
}


class Carousel extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            currIndex: 0,
            translateValue: 0
        };
        this.nextSlide = this.nextSlide.bind(this);
        this.prevSlide = this.prevSlide.bind(this);
      }

    prevSlide () {
        const lastIndex = carouselThumbnails.length - 1;
        const { currIndex } = this.state;
        const shouldResetIndex = currIndex === 0;
        const index =  shouldResetIndex ? lastIndex : currIndex - 1;
        console.log(index);
        this.setState({
            currIndex: index
        });
    }

    nextSlide () {
        const lastIndex = carouselThumbnails.length - 1;
        const { currIndex } = this.state;
        const shouldResetIndex = currIndex === lastIndex;
        const index =  shouldResetIndex ? 0 : currIndex + 1;

        this.setState({
            currIndex: index
        });
    }

    render () {
        const themes = [blueTheme, tealTheme, goldTheme, grayTheme];
        carouselThumbnails.map((value, index) => {
            value.theme = themes[index];
            return value;
        });
        const thumbnail = carouselThumbnails[this.state.currIndex];
        const prevThumbnail = carouselThumbnails[thumbnail.prev - 1];
        const nextThumbnail = carouselThumbnails[thumbnail.next - 1];

        return (
            <div style={{display: "grid", gridTemplateColumns: "500px 500px 500px"}}>
            <Arrow
                id="left"
                onClick={ this.prevSlide }>
                &#60;
            </Arrow>
            <Arrow right
                id="right"
                onClick={ this.nextSlide }>
                &#62;
            </Arrow>
            <SlideWrapper  style={{gridColumn: "1 / 2"}}>
                <ThemeProvider theme={prevThumbnail.theme}>
                        <ImageSlide src={prevThumbnail.imgUrl} />
                        <OverLay>
                            <ImgThumbnail src={prevThumbnail.imgUrl}></ImgThumbnail>
                            <Title>{prevThumbnail.title}</Title>
                            <TextThumbnail>{prevThumbnail.description}</TextThumbnail>
                            <ButtonContainer>
                                <Button>Buy Now</Button>
                                <TrailerButon>Watch Trailer</TrailerButon>
                            </ButtonContainer>
                        </OverLay>
                </ThemeProvider>
            </SlideWrapper>
            <SlideWrapper style={{gridColumn: "2 / 3"}}>
                <ThemeProvider theme={thumbnail.theme}>
                        <ImageSlide src={thumbnail.imgUrl} />
                        <OverLay>
                            <ImgThumbnail src={thumbnail.imgUrl}></ImgThumbnail>
                            <Title>{thumbnail.title}</Title>
                            <TextThumbnail>{thumbnail.description}</TextThumbnail>
                            <ButtonContainer>
                                <Button>Buy Now</Button>
                                <TrailerButon>Watch Trailer</TrailerButon>
                            </ButtonContainer>
                        </OverLay>
                </ThemeProvider>
            </SlideWrapper>
            <SlideWrapper style={{gridColumn: "3 / 4"}}>
                <ThemeProvider theme={nextThumbnail.theme}>
                        <ImageSlide src={nextThumbnail.imgUrl} />
                        <OverLay>
                            <ImgThumbnail src={nextThumbnail.imgUrl}></ImgThumbnail>
                            <Title>{nextThumbnail.title}</Title>
                            <TextThumbnail>{nextThumbnail.description}</TextThumbnail>
                            <ButtonContainer>
                                <Button>Buy Now</Button>
                                <TrailerButon>Watch Trailer</TrailerButon>
                            </ButtonContainer>
                        </OverLay>
                </ThemeProvider>
            </SlideWrapper>
            </div>

        );
    }
  }

  export default Carousel;
