import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import device from "./styles/device";
import sizes from "./styles/device-sizes";
import colors from "./styles/palette";
import carouselThumbnails from "../assets/carouselThumbnails";

//TODO: this should be a function that takes in a list of images and does the same thing for all of them

const ImageSlide = styled.img`
    width: 100%;
    -webkit-filter: blur(10px);
    filter: blur(10px);
    height: 500px;
    margin: 0px;
    padding: 0px;
    grid-row: 1 / 2;
    grid-column: 1 / 5;
`;



// const Arrow = ({ direction, clickFunction, glyph }) => (
//     <div
//       className={ `slide-arrow ${direction}` }
//       onClick={ clickFunction }>
//       { glyph }
//     </div> 
//   );

const CarouselWrapper = styled.div`
    //      |<|img|text|>|


`;

const OverLay = styled.div`
    grid-template-columns: auto 300px 700px auto;
    grid-template-rows: auto;
    background-color: ${props => props.theme.main};
    display: grid;  
    align-content: center;
    justify-items: center;
    align-items: center;
    justify-content: center;
    // grid-column: 1 / 5;
    // grid-row: 1 / 2;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 500px;
    margin: 0px;
    padding: 0px;
`;

const Arrow = styled.div`
    // position: absolute;
    grid-column: ${props => props.right ? "4 / 5" : "1 / 2"};
    // top: 100px;
    color: gray;
    // left: 10px;
    font-size: 70px;
    width: 100%;

    :hover {
        cursor: pointer;
        color: ${colors.teal1};
    }
`;

const ImgThumbnail = styled.img`
    width: 150px;
    height: 250px;
    grid-column: 2 / 3;
`;

const goldTheme = {
    main: colors.gold1,
    transparent: colors.gold1Transparent
}

const blueTheme = {
    main: colors.blue1,
    transparent: colors.blue1Transparent
}

const tealTheme = {
    main: colors.teal1,
    transparent: colors.teal1Transparent
}

const grayTheme = {
    main: colors.charcoal1,
    transparent: colors.charcoal1Transparent
}

class Carousel extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
          currentImageIndex: 0
        };
        this.nextSlide = this.nextSlide.bind(this);
        this.previousSlide = this.previousSlide.bind(this);
      }

    previousSlide () {
        const lastIndex = imgUrls.length - 1;
        const { currentImageIndex } = this.state;
        const shouldResetIndex = currentImageIndex === 0;
        const index =  shouldResetIndex ? lastIndex : currentImageIndex - 1;

        this.setState({
            currentImageIndex: index
        });
    }

    nextSlide () {
        const lastIndex = imgUrls.length - 1;
        const { currentImageIndex } = this.state;
        const shouldResetIndex = currentImageIndex === lastIndex;
        const index =  shouldResetIndex ? 0 : currentImageIndex + 1;

        this.setState({
            currentImageIndex: index
        });
    }

    render () {
        console.log("hi");
        console.log(imgUrls[0]);
      return (
        <ThemeProvider theme={carouselThumbnails[this.state.currentImageIndex].theme}>
          <ImageSlide src={carouselThumbnails[this.state.currentImageIndex].imgUrl} />
          <OverLay>
            <Arrow
                id="left"
                onClick={ this.prevSlide }> &#60; </Arrow>
            <ImgThumbnail src={imgUrls[this.state.currentImageIndex]}></ImgThumbnail>
            {/* <TextThumbnail>{textBlurb[]}</TextThumbnail> */}
            <Arrow right
                id="right"
                onClick={ this.nextSlide }>
                &#62;
                </Arrow>
          </OverLay>
        </ThemeProvider>
      );
    }
  }

  export default Carousel;
