//USE A GRID!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
//margins should be flexible, content columns static
//use standard breakpoints
//consider how to change order...

const size = {
  mobileS: '320px',
  mobileM: '375px',
  mobileL: '425px',
  tablet: '768px',
  laptop: '1024px',
  laptopL: '1440px',
  desktop: '2560px'
}

const thumbnailStyle = {
  display: "grid",
  gridTemplateColumns: "600px 600px",
  // flexDirection: "row",
  // justifyContent: "space-evenly",
  alignItems: "center",
  // flexWrap: "wrap",
  width: "100%",
  padding: "30px"
};

