    //         <TextBlurb title={menuItems[0].title} text={menuItems[0].text}/>
    //       </Grid>
    //     </React.Fragment>
    //   );
    // }
    return (
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "100%",
          gridTemplateRows: "300px 300px 300px",
          justifyContent: "center",
          alignContent: "center"
        }}
      >
        <div style={{gridRow:"2 /3", gridColumn: "1 / 6", gridTemplateColumns: "auto 500px 30px 500px auto", display: "grid"}}>
          <img src={menuItems[0].imageSrc}
              style={{backgroundColor: "#3a3a3a",
                      color: "white",
                      gridColumn: "2 / 3"}}></img>
          <TextBlurb 
            style={{backgroundColor: "#3a3a3a",
                    color: "white",
                    gridColumn: "4 / 5"}}
            title={menuItems[0].title} 
            text={menuItems[0].text}/>
        </div>
        <div style={{gridRow:"2 /3", gridColumn: "1 / 6", gridTemplateColumns: "auto 500px 30px 500px auto", display: "grid"}}>
          <img src={menuItems[0].imageSrc}
