import React from "react";
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import device from "./device";

const ThumbnailWrapper = styled.div`
  display: grid;
  justify-content: center;
  align-content: center;
  align-items: center;

  @media ${device.tablet} {
    padding: 100px;
    grid-template-columns: auto 600px auto;
    grid-template-rows: auto auto;
  }

  @media ${device.laptop} {
    grid-template-columns: auto 500px 50px 500px auto;
    grid-template-rows: auto;
  }
`;

const Left = styled.div`
    @media (min-width: 700px) {
        grid-column: 2 / 3;
        grid-row: 2 / 3;
    }

    @media (min-width: 1220px){
        width: 500px;
        grid-column: 2 / 3;
        grid-row: 1 / 2;
    }
`;

const Right = styled.div`
    @media (min-width: 700px) {
        grid-column: 2 / 3;
        grid-row: 1 / 2;
    }

    @media (min-width: 1220px){
        grid-column: 4 / 5;
        grid-row: 1 / 2;
    }
`;


function renderOrder(props) {
    if(props.imgFirst || (window.innerWidth < 1220)) {
      return (
        <React.Fragment>
            <Left><img src={props.imgSrc}></img></Left>
            <Right>
                <TextBlurb 
                    title={props.title} 
                    text={props.text}>
                </TextBlurb>
            </Right>
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
            <Left>
                <TextBlurb 
                    title={props.title} 
                    text={props.text}>
                </TextBlurb>
            </Left>
            <Right> <img src={props.imgSrc}></img></Right>
        </React.Fragment>
      );
    }
  }

export const Thumbnail = ({ ...props }) => (
    <ThumbnailWrapper style={props.style}>
        {renderOrder(props)}
    </ThumbnailWrapper>
)

export default Thumbnail;
