import React, { PropTypes, Component } from 'react';
import TextBlurb from "./textBlurb";
import styled from "styled-components";
import { ThemeProvider } from "styled-components";
import device from "./styles/device";
// import sizes from "./jjnn/device-sizes";
import colors from "./styles/palette";
import carouselThumbnails from "../assets/carouselThumbnails";


const ImageSlide = styled.img`
    width: 100vw;
    -webkit-filter: blur(10px) brightness(40%);
    filter: blur(10px) brightness(40%);
    height: 500px;
    margin: 0px;
    padding: 0px;
`;

const OverLay = styled.div`
    grid-template-columns: auto 300px 500px auto;
    grid-template-rows: auto auto auto;
    background-color: ${props => props.theme.transparent};
    display: grid;  
    align-content: center;
    align-items: center;
    justify-content: center;
    position: relative;
    margin-top: -500px;

    width: 100vw;
    height: 500px;
    padding: 0px;
`;

const Arrow = styled.div`
    padding: 0px 50px;
    color: ${colors.white1Transparent};
    font-size: 70px;
    position: absolute;
    top: 150px;
    left: ${props => props.right ? "80%" : "10%"};

    z-index: 9999;
    :hover {
        cursor: pointer;
        color: ${colors.blue1};
    }
`;

const Title = styled.h1 `
    color: ${colors.white1};
    margin: 0px;
    padding: 0px;
    font-weight: 500;
    grid-column: 3 / 4;
`;

const TextThumbnail = styled.p`
    margin: 0px;
    padding: 0px;
    color: ${colors.white1Transparent};
    grid-column: 3 / 4;
`;

const ImgThumbnail = styled.img`
    justify-self: flex-end;
    width: 200px;
    margin: 50px;
    height: auto;
    grid-column: 2 / 3;
    grid-row: 1 / 4;
`;

const ButtonContainer = styled.div`
    grid-column: 3 / 4;
    grid-row: 3 / 4;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    border-radius: 5px;
    margin: 10px;
    color: ${colors.white1};
    background-color: ${props => props.theme.main};
    border: none;
    font-size: 17px;
    font-weight: bold;
`;

const TrailerButton = styled(Button)`
    background-color: ${colors.transparent};
    border-color: ${colors.white1Transparent};
    border: 1px solid ${colors.white1Transparent};
`;

const CarouselContainer = styled.div`
    display: flex;
    transition: ${(props) => props.sliding ? 'none' : 'transform 0.6s ease'};
    transform: ${(props) => {
        if (!props.sliding) return 'translateX(calc(-100%))'
        if (props.direction === 'prev') return 'translateX(calc(2 * (-100%)))'
        return 'translateX(0%)'
    }};
`

const Wrapper = styled.div`
    width: 100%;
    overflow: hidden;
`

const CarouselSlot = styled.div`
    flex: 1 0 100%;
    flex-basis: 100%;
    order: ${(props) => props.order};
`

const goldTheme = {
    main: colors.gold1,
    transparent: colors.gold1Transparent
}

const blueTheme = {
    main: colors.blue1,
    transparent: colors.blue1Transparent
}

const tealTheme = {
    main: colors.teal1,
    transparent: colors.teal1Transparent
}

const grayTheme = {
    main: colors.charcoal3,
    transparent: colors.charcoal1Transparent
}

class Carousel extends Component {
    constructor (props) {
        super(props);

        this.state = {
            currIndex: 0,
            slideDirection: 'next',
            sliding: false
        };
        this.nextSlide = this.nextSlide.bind(this);
        this.prevSlide = this.prevSlide.bind(this);
        this.doSliding = this.doSliding.bind(this);
      }

    doSliding(currIndex, direction) {
        this.setState({
            currIndex: currIndex,
            slideDirection: direction,
            sliding: true
        })
        setTimeout(() => {
         this.setState({
            sliding: false
          })
        }, 50)
      }

    prevSlide () {
        const curr = this.state.currIndex;
        const numItems = carouselThumbnails.length
        this.doSliding(curr === 0 ? numItems - 1 : curr - 1, 'prev')
    }

    nextSlide () {
        const curr = this.state.currIndex
        const numItems = carouselThumbnails.length || 1;
        this.doSliding(curr === numItems - 1 ? 0 : curr + 1, 'next')
    }

    getOrder(itemIndex) {
        const curr = this.state.currIndex
        const numItems = carouselThumbnails.length || 1
        if (itemIndex - curr < 0) {
          return numItems - Math.abs(itemIndex - curr)
        }
        return itemIndex - curr
      }

    render () {
        const themes = [blueTheme, tealTheme, goldTheme, blueTheme, grayTheme];
        carouselThumbnails.map((value, index) => {
            value.theme = themes[index];
            return value;
        });
        return (
            <Wrapper>
                <Arrow right
                    id="right"
                    onClick={ this.nextSlide }>
                    &#62;
                </Arrow>
                <Arrow 
                    id="left"
                    onClick={ this.prevSlide }>
                    &#60;
                </Arrow>
            <CarouselContainer sliding={this.state.sliding} direction={this.state.slideDirection}>
            {
                carouselThumbnails.map((thumbnail, index) =>
                    // <div id={index}>
                    <CarouselSlot id={index} order={ this.getOrder(index) }>
                        <ThemeProvider theme={thumbnail.theme}>
                            <ImageSlide src={thumbnail.imgUrl} />
                            <OverLay>

                                <ImgThumbnail src={thumbnail.imgUrl}></ImgThumbnail>
                                <Title>{thumbnail.title}</Title>
                                <TextThumbnail>{thumbnail.description}</TextThumbnail>
                                <ButtonContainer>
                                    <Button>Buy Now</Button>
                                    <TrailerButton>Watch Trailer</TrailerButton>
                                </ButtonContainer>

                            </OverLay>
                        </ThemeProvider>


                    </CarouselSlot>
                )
            }
            </CarouselContainer>
            </Wrapper>

        );
    }
  }

  export default Carousel;
